f32_t modulo(f32_t value, f32_t mod) {
	if (mod < 0.0f)
		mod = -mod;
	if (value < 0.0f) {
		while (value < mod)
			value += mod;
	} else {
		while (value > mod)
			value -= mod;
	}
	return value;
}

f32_t sin(f32_t angle) {
	return lut_sin[(size_t) (modulo(angle, PIm2) * 1023.0f / PIm2)];
}

f32_t cos(f32_t angle) {
	return sin(angle + PIm2);
}

f32_t asin(f32_t value) {
	if (value < 0.0f) {
		return -lut_asin[(size_t) (modulo(-value, 1.0f) * 1023.0f)];
	} else {
		return lut_asin[(size_t) (modulo(value, 1.0f) * 1023.0f)];
	}
}

f32_t acos(f32_t value) {
	return PId2 - asin(value);
}

f32_t sqrt(f32_t value) {
	f32_t res = value / 2.0f;
	for (size_t i = 0; i < SQRT_ITERATIONS; i++) {
		res = (res + value / res) / 2.0f;
	}
	return res;
}
